package com.brawl.logic.message.admin;import com.brawl.logic.message.PiranhaMessage;import com.brawl.server.network.ClientConnection;import com.brawl.server.network.TCPServer;import com.brawl.titan.Messaging.State;public class StartShutdownMessage extends PiranhaMessage {    @Override    public void process(ClientConnection clientConnection) throws Exception {        new ShutdownThread().start();    }    @Override    public State getRequiredState() {        return State.HOME;    }    public static class ShutdownThread extends Thread {        @Override        public void run() {            TCPServer.shutdown();        }    }}